mapper.delete:
update party
set PARTY_AVAILABLE = false
where party_id = ${PARTY_ID}

mapper.select:
SELECT * FROM PARTY WHERE 1=1 AND PARTY_AVAILABLE = TRUE
AND CATEGORY_NAME = #{CATEGORY_NAME}
AND PARTY_TITLE LIKE CONCAT('%',#{SEARCH_KEYWORD},'%')
LIMIT #{LIST_SIZE} OFFSET #{START_ROW}

select count(*) from party WHERE 1=1 AND PARTY_AVAILABLE = TRUE
AND CATEGORY_NAME = #{CATEGORY_NAME}
AND PARTY_TITLE LIKE CONCAT('%',#{SEARCH_KEYWORD},'%')
